import React, { useRef, useState, useEffect } from "react";
import { MapContainer, Marker, Popup, TileLayer } from "react-leaflet";
import 'leaflet/dist/leaflet.css';
import 'leaflet-defaulticon-compatibility/dist/leaflet-defaulticon-compatibility.webpack.css';
import L from 'leaflet';
import 'leaflet-defaulticon-compatibility';

const url = 'http://localhost:8080/objects';

const LeafletMap = () => {
    const mapRef = useRef(null);
    const [markers, setMarkers] = useState([]);

    useEffect(() => {
        // Fetch data from API and set markers
        const fetchMarkers = async () => {
            try {
                const response = await fetch(url);
                const data = await response.json();
                setMarkers(data); // Assuming `data` is an array of objects with lat and lng
            } catch (error) {
                console.error("Error fetching markers:", error);
            }
        };

        fetchMarkers();
    }, []); // Run only once when the component mounts

    const latitude = 50.83106;
    const longitude = -0.13352;

    return (
        <MapContainer 
            center={[latitude, longitude]} 
            zoom={15} 
            ref={mapRef} 
            style={{ height: "100vh", width: "100vw" }}
        >
            <TileLayer
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {markers.map((marker, index) => (
                <Marker key={index} position={[marker.latitude, marker.longitude]}>
                    <Popup>
                        {marker.name || "No name provided"} {/* Customize with API data */}
                    </Popup>
                </Marker>
            ))}
        </MapContainer>
    );
};

export default LeafletMap;
